import React, { useState } from "react";
import "../styles/custom.css";

const CreateRecipe = () => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    ingredients: "",
    steps: "",
    image: null,
    video: null,
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Recipe Created:", formData);
  };

  return (
    <div className="create-recipe-container">
      <div className="create-recipe-overlay"></div>
      <div className="create-recipe-content">
        <h2>Create a New Recipe</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" name="title" placeholder="Recipe Title" required onChange={handleChange} />
          <textarea name="description" placeholder="Short Description" required onChange={handleChange}></textarea>
          <textarea name="ingredients" placeholder="Ingredients (comma separated)" required onChange={handleChange}></textarea>
          <textarea name="steps" placeholder="Steps" required onChange={handleChange}></textarea>
          <input type="file" name="image" accept="image/*" onChange={handleFileChange} />
          <input type="file" name="video" accept="video/*" onChange={handleFileChange} />
          <button type="submit" className="btn btn-primary">Submit Recipe</button>
        </form>
      </div>
    </div>
  );
};

export default CreateRecipe;
